import React, { useReducer } from "react";
import CartContext from "./cart-context";

const cartItem = { item: [], totalAmount: 0 };

const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

    const presentCartItemIndex = state.item.findIndex(
      (item) => item.id === action.item.id
    );
    console.log(state.item[presentCartItemIndex]);
    // const existingCartItem= state.item[presentCartItemIndex];
    const updatedItems = state.item.concat(action.item);

    return {
      item: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  if (action.type === "REMOVE") {
  }
  return cartItem;
};
const CartProvider = (props) => {
  const [cartState, dispatchCart] = useReducer(cartReducer, cartItem);
  const addItemToCart = (item) => {
    dispatchCart({ type: "ADD", item: item });
  };
  const removeItemFromCart = (id) => {
    dispatchCart({ type: "REMOVE", id: id });
  };

  const cartContext = {
    item: cartState.item,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCart,
    removeItem: removeItemFromCart,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
