import React from "react";
import Input from "../../UI/Input";
import "./MealItemForm.css";
const MealItemForm = (props) => {
  return (
    <div>
      <form className="form">
        <Input
          label="Amount"
          input={{
            it: "amount",
            type: "number",
            min: "1",
            max: "5",
            default: "1",
          }}
        />
        <button>+ Add</button>
      </form>
    </div>
  );
};

export default MealItemForm;
